import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Email {
  id: string;
  from: string;
  subject: string;
  body: string;
}

interface EmailOverviewProps {
  onSelectEmail: (id: string) => void;
}

const EmailOverview: React.FC<EmailOverviewProps> = ({ onSelectEmail }) => {
  const [emails, setEmails] = useState<Email[]>([]);

  useEffect(() => {
    const fetchEmails = async () => {
      const response = await axios.get('http://localhost:3000/emails');
      setEmails(response.data);
    };
    fetchEmails();
  }, []);

  return (
    <div className="email-overview">
      <h2>Email Overview</h2>
      {emails.map((email) => (
        <div key={email.id} className="email-item" onClick={() => onSelectEmail(email.id)}>
          <p className="email-from">{email.from}</p>
          <p className="email-subject">{email.subject}</p>
          <p className="email-preview">{email.body}</p>
        </div>
      ))}
    </div>
  );
};

export default EmailOverview;
