import React from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import EmailOverview from './components/emailOverview';
import MessageSidebar from './components/messageSidebar';
import './App.css';

const App: React.FC = () => {
  const [selectedEmailId, setSelectedEmailId] = React.useState<string | null>(null);

  return (
    <Router>
      <div className="app">
        <EmailOverview onSelectEmail={setSelectedEmailId} />
        <MessageSidebar selectedEmailId={selectedEmailId} />
      </div>
    </Router>
  );
};

export default App;
